import React, { Component } from 'react';
import io from 'socket.io-client';
import moment from 'moment'; // Подключите moment, если еще не подключили

class Bot extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isChatbotOpen: false, // Изначально окно чат-бота скрыто
      clientsTotal: 0
    };

    this.socket = io('localhost:3000');// Подключаемся к сокетам
  }

  componentDidMount() {
    // Обработка событий сокетов
    this.socket.on('clients-total', (data) => {
      this.setState({ clientsTotal: data });
    });

    this.socket.on('chat-message', (data) => {
      this.messageTone.play();
      this.addMessageToUI(false, data);
    });

    this.socket.on('feedback', (data) => {
      this.clearFeedback();
      const element = `
        <li class="message-feedback">
          <p class="feedback" id="feedback">${data.feedback}</p>
        </li>
      `;
      document.getElementById('message-container').innerHTML += element;
    });
  }

  toggleChatbot = () => {
    this.setState(prevState => ({
      isChatbotOpen: !prevState.isChatbotOpen // Инвертируем состояние окна чат-бота
    }));
  }

  sendMessage = () => {
    const messageInput = document.getElementById('message-input');
    if (messageInput.value === '') return;

    const data = {
      name: document.getElementById('name-input').value,
      message: messageInput.value,
      dateTime: new Date(),
    };

    this.socket.emit('message', data);
    this.addMessageToUI(true, data);
    messageInput.value = '';
  }

  addMessageToUI = (isOwnMessage, data) => {
    this.clearFeedback();
    const element = `
      <li class="${isOwnMessage ? 'message-right' : 'message-left'}">
        <p class="message">
          ${data.message}
          <span>${data.name} ● ${moment(data.dateTime).fromNow()}</span>
        </p>
      </li>
    `;

    document.getElementById('message-container').innerHTML += element;
    this.scrollToBottom();
  }

  scrollToBottom = () => {
    const messageContainer = document.getElementById('message-container');
    messageContainer.scrollTo(0, messageContainer.scrollHeight);
  }

  clearFeedback = () => {
    document.querySelectorAll('li.message-feedback').forEach((element) => {
      element.parentNode.removeChild(element);
    });
  }

  render() {
    const { isChatbotOpen, clientsTotal } = this.state;

    return (
      <div className="bot-container">
        <button className="chatbot-button" onClick={this.toggleChatbot}>
          {isChatbotOpen ? 'Закрыть Чат' : 'Открыть Чат'}
        </button>

        <div className={`bot ${isChatbotOpen ? 'active' : ''}`}>
          <div className="name">
            <span>
              <i className="far fa-user" />
            </span>
            <input
              type="text"
              id="name-input"
              className="name-input"
              defaultValue="anonymous"
              maxLength={20}
            />
          </div>
          <ul className="message-container" id="message-container">
            <li className="message-left">
              <p className="message">
                Hi
                <span></span>
              </p>
            </li>
            <li className="message-right">
              <p className="message">
                Hey
                <span></span>
              </p>
            </li>
            <li className="message-feedback">
              <p className="feedback" id="feedback">
              </p>
            </li>
          </ul>

          <form className="message-form" id="message-form" onSubmit={(e) => {
            e.preventDefault();
            this.sendMessage();
          }}>
            <input
              type="text"
              name="message"
              id="message-input"
              className="message-input"
            />
            <div className="v-divider" />
            <button type="submit" className="send-button">
              send <span />
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default Bot;
