import React, { useState } from 'react';
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from './components/Items';
import io from 'socket.io-client';
import Bot from './components/Bot';

function App() {
  const socket = io()


const messageContainer = document.getElementById('message-container')
const nameInput = document.getElementById('name-input')
const messageForm = document.getElementById('message-form')
const messageInput = document.getElementById('message-input')

const messageTone = new Audio('/message-tone.mp3')
  const [orders, setOrders] = useState([]);
  const [items] = useState([
        {
          id: 1,
          title: 'Композиция Desire Me',
          img: 'choco1.jfif',
          desc: '',
          category: '',
          price: '15500'
        },
        {
          id: 2,
          title: 'Композиция Бланш',
          img: 'choco2.jfif',
          desc: '',
          category: '',
          price: '8990'
        },
        {
          id: 3,
          title: 'Композиция Шоколадный Прованс',
          img: 'choco3.jfif',
          desc: '',
          category: '',
          price: '6990'
        },
        {
          id: 4,
          title: 'Композиция Красота Любви',
          img: 'choco4.jfif',
          desc: '',
          category: '',
          price: '11000'
        },
        {
          id: 5,
          title: 'Композиция Вдохновение',
          img: 'choco5.jfif',
          desc: '',
          category: '',
          price: '5990'
        },
        {
          id: 6,
          title: 'Композиция Клубничный Сюрприз',
          img: 'choco6.jfif',
          desc: '',
          category: '',
          price: '8000'
        },
        {
          id: 7,
          title: 'Композиция Вояж',
          img: 'choco7.jfif',
          desc: '',
          category: '',
          price: '11990'
        },
        {
          id: 8,
          title: 'Композиция Романтик',
          img: 'choco8.jfif',
          desc: '',
          category: '',
          price: '7990'
        },
        {
          id: 9,
          title: 'Композиция Полярные Сны',
          img: 'choco9.jfif',
          desc: '',
          category: '',
          price: '19000'
        },
        {
          id: 10,
          title: 'Композиция Эльда',
          img: 'choco10.jfif',
          desc: '',
          category: '',
          price: '13000'
        },
        {
          id: 11,
          title: 'Композиция Одно Желание',
          img: 'choco11.jfif',
          desc: '',
          category: '',
          price: '8000'
        },
        {
          id: 12,
          title: 'Композиция Клубничная Мелодия',
          img: 'choco12.jfif',
          desc: '',
          category: '',
          price: '10990'
        },
        {
          id: 13,
          title: 'Композиция Нежное Сердце',
          img: 'choco13.jfif',
          desc: '',
          category: '',
          price: '24990'
        },
        {
          id: 14,
          title: 'Композиция Ферроль',
          img: 'choco14.jfif',
          desc: '',
          category: '',
          price: '12000'
        },
        {
          id: 15,
          title: 'Композиция Мелита',
          img: 'choco15.jfif',
          desc: '',
          category: '',
          price: '7990'
        },
      ]);

  const deleteOrder = (id) => {
    setOrders(orders.filter(el => el.id !== id));
  }

  const addToOrder = (item) => {
    let isInArray = false;
    orders.forEach(el => {
      if (el.id === item.id) {
        isInArray = true;
      }
    });
    if (!isInArray) {
      setOrders([...orders, item]);
    }
  }

  messageForm.addEventListener('submit', (e) => {
    e.preventDefault()
    sendMessage()
  })

  function sendMessage() {
    if (messageInput.value === '') return
    // console.log(messageInput.value)
    const data = {
      name: nameInput.value,
      message: messageInput.value,
      dateTime: new Date(),
    }
    socket.emit('message', data)
    addMessageToUI(true, data)
    messageInput.value = ''
  }

  socket.on('chat-message', (data) => {
    // console.log(data)
    messageTone.play()
    addMessageToUI(false, data)
  })

  function addMessageToUI(isOwnMessage, data) {
    clearFeedback()
    const element = `
        <li class="${isOwnMessage ? 'message-right' : 'message-left'}">
            <p class="message">
              ${data.message}
              <span>${data.name} ● ${moment(data.dateTime).fromNow()}</span>
            </p>
          </li>
          `

    messageContainer.innerHTML += element
    scrollToBottom()
  }

  function scrollToBottom() {
    messageContainer.scrollTo(0, messageContainer.scrollHeight)
  }

  messageInput.addEventListener('focus', (e) => {
    socket.emit('feedback', {
      feedback: `✍️ ${nameInput.value} is typing a message`,
    })
  })

  messageInput.addEventListener('keypress', (e) => {
    socket.emit('feedback', {
      feedback: `✍️ ${nameInput.value} is typing a message`,
    })
  })
  messageInput.addEventListener('blur', (e) => {
    socket.emit('feedback', {
      feedback: '',
    })
  })

  socket.on('feedback', (data) => {
    clearFeedback()
    const element = `
          <li class="message-feedback">
            <p class="feedback" id="feedback">${data.feedback}</p>
          </li>
    `
    messageContainer.innerHTML += element
  })

  function clearFeedback() {
    document.querySelectorAll('li.message-feedback').forEach((element) => {
      element.parentNode.removeChild(element)
    })
  }

  return (
    <div className="wrapper">
      <Header orders={orders} onDelete={deleteOrder}/>
      <Items items={items} onAdd={addToOrder}/>
      <Bot />
      <Footer />
    </div>
  );
}

export default App;
