        {
          id: 15,
          title: 'Композиция Мелита',
          img: 'choco15.jfif',
          desc: '',
          category: '',
          price: '7990'
        },
      ]);

  const deleteOrder = (id) => {
    setOrders(orders.filter(el => el.id !== id));
  }

  const addToOrder = (item) => {
    let isInArray = false;
    orders.forEach(el => {
      if (el.id === item.id) {
        isInArray = true;
