import React, { Component } from 'react';

class Bot extends Component {
    constructor(props) {
      super(props);
      this.state = {
        isChatbotOpen: false // Изначально окно чат-бота скрыто
      };
    }
  
    toggleChatbot = () => {
      this.setState(prevState => ({
        isChatbotOpen: !prevState.isChatbotOpen // Инвертируем состояние окна чат-бота
      }));
    }
  
    render() {
      const { isChatbotOpen } = this.state;
  
      return (
        <>
          <button id="chatbotButton" onClick={this.toggleChatbot}>
            {isChatbotOpen ? 'Close Chatbot' : 'Open Chatbot'}
          </button>
  
          {isChatbotOpen && (
        <div className="bot">
        <div className="name">
          <span>
            <i className="far fa-user" />
          </span>
          <input
            type="text"
            id="name-input"
            className="name-input"
            defaultValue="anonymous"
            maxLength={20}
          />
        </div>
        <ul className="message-container" id="message-container">
          <li className="message-left">
            <p className="message">
              lorem impsun
              <span>bluebird 25 July 10:40</span>
            </p>
          </li>
          <li className="message-right">
            <p className="message">
              lorem impsun
              <span>bluebird 25 July 10:40</span>
            </p>
          </li>
          <li className="message-feedback">
            <p className="feedback" id="feedback">
              killer is typing a message
            </p>
          </li>
        </ul>

        <form className="message-form" id="message-form">
            <input
                type="text"
                name="message"
                id="message-input"
                className="message-input"
            />
            <div className="v-divider" />
            <button type="submit" className="send-button">
                send <span />
            </button>
        </form>

        </div>
        )}
      </>
    );
  }
}

export default Bot
