import React from 'react';
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from './components/Items';

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      orders: [], 
      items: [
        {
          id: 1,
          title: 'Композиция Desire Me',
          img: 'choco1.jfif',
          desc: '',
          category: '',
          price: '15.500'
        },
        {
          id: 2,
          title: 'Композиция Бланш',
          img: 'choco2.jfif',
          desc: '',
          category: '',
          price: '8.990'
        },
        {
          id: 3,
          title: 'Композиция Шоколадный Прованс',
          img: 'choco3.jfif',
          desc: '',
          category: '',
          price: '6.990'
        },
        {
          id: 4,
          title: 'Композиция Красота Любви',
          img: 'choco4.jfif',
          desc: '',
          category: '',
          price: '11.000'
        },
        {
          id: 5,
          title: 'Композиция Вдохновение',
          img: 'choco5.jfif',
          desc: '',
          category: '',
          price: '5.990'
        },
        {
          id: 6,
          title: 'Композиция Клубничный Сюрприз',
          img: 'choco6.jfif',
          desc: '',
          category: '',
          price: '8.000'
        },
        {
          id: 7,
          title: 'Композиция Вояж',
          img: 'choco7.jfif',
          desc: '',
          category: '',
          price: '11.990'
        },
        {
          id: 8,
          title: 'Композиция Романтик',
          img: 'choco8.jfif',
          desc: '',
          category: '',
          price: '7.990'
        },
        {
          id: 9,
          title: 'Композиция Полярные Сны',
          img: 'choco9.jfif',
          desc: '',
          category: '',
          price: '19.000'
        },
      ]
    }
    this.addToOrder = this.addToOrder.bind(this)
    this.deleteOrder = this.deleteOrder.bind(this)
  }
  render() {
    return (
      <div className="wrapper">
        <Header orders={this.state.orders} onDelete={this.deleteOrder}/>
        <Items items={this.state.items} onAdd={this.addToOrder}/>
        <Footer />
      </div>
    );
  }

  deleteOrder(id) {
    this.setState({orders: this.state.orders.filter(el => el.id !== id)})
  }

  addToOrder(item) {
    let isInArray = false
    this.state.orders.forEach(el => {
      if (el.id === item.id)
        isInArray = true
    })
    if (!isInArray)
      this.setState({orders: [...this.state.orders, item]})
  }
}

export default App;
